const popupButtons=document.querySelectorAll(".js-popupButton"),closePopup=document.querySelectorAll(".js-closePopup"),popup=document.querySelectorAll(".js-popup"),popupBody=document.querySelectorAll(".js-popupBody"),counter=document.querySelectorAll(".js-counter");for(let e=0;e<popupButtons.length;e++){let o=0;popupButtons[e].addEventListener("click",()=>{popup[e].classList.add("open"),o++,counter[e].innerHTML=o+" times"})}closePopup.forEach(o=>{o.addEventListener("click",()=>{popup.forEach(o=>{o.classList.remove("open")})})}),popup.forEach(e=>{e.addEventListener("click",o=>{o.target.classList.contains("js-popupBody")&&e.classList.remove("open")})});