const popupButton=document.querySelector(".js-popupButton"),closePopup=document.querySelector(".js-closePopup"),popup=document.querySelector(".js-popup"),popupBody=document.querySelector(".js-popupBody");popupButton.addEventListener("click",p=>{p.preventDefault(),popup.classList.add("open")}),closePopup.addEventListener("click",()=>{popup.classList.remove("open")}),popup.addEventListener("click",p=>{p.target.classList.contains("js-popupBody")&&popup.classList.remove("open")});